+/* Daily Commits Page - Enhanced Controls */
+.daily-controls-section {
+    margin-bottom: 30px;
+}
+
+.controls-container {
+    display: grid;
+    grid-template-columns: 1fr 1fr;
+    gap: 20px;
+}
+
+.control-card {
+    background: var(--bg-secondary);
+    border: 1px solid var(--border-color);
+    border-radius: 12px;
+    overflow: hidden;
+    transition: all 0.3s ease;
+}
+
+.control-card:hover {
+    border-color: var(--accent-green);
+    box-shadow: 0 4px 12px rgba(57, 255, 20, 0.1);
+}
+
+.card-header {
+    background: rgba(57, 255, 20, 0.05);
+    padding: 15px 20px;
+    border-bottom: 1px solid var(--border-color);
+}
+
+.card-header h3 {
+    margin: 0;
+    font-size: 1.1rem;
+    color: var(--text-primary);
+    display: flex;
+    align-items: center;
+    gap: 8px;
+}
+
+.card-content {
+    padding: 20px;
+}
+
+/* Date Inputs Section */
+.date-inputs {
+    display: flex;
+    align-items: center;
+    gap: 15px;
+    margin-bottom: 20px;
+}
+
+.input-group {
+    flex: 1;
+}
+
+.input-group label,
+.user-select-group label,
+.specific-date-section label {
+    display: block;
+    font-size: 0.85rem;
+    color: var(--text-secondary);
+    margin-bottom: 8px;
+    font-weight: 500;
+    text-transform: uppercase;
+    letter-spacing: 0.5px;
+}
+
+.date-input,
+.user-select {
+    width: 100%;
+    padding: 10px 15px;
+    background: var(--bg-primary);
+    border: 1px solid var(--border-color);
+    border-radius: 6px;
+    color: var(--text-primary);
+    font-size: 0.95rem;
+    transition: all 0.2s ease;
+}
+
+/* Fix calendar icon visibility - force white icons */
+.date-input::-webkit-calendar-picker-indicator {
+    filter: invert(1) brightness(2);
+    opacity: 0.8;
+    cursor: pointer;
+    transition: opacity 0.2s ease;
+}
+
+.date-input::-webkit-calendar-picker-indicator:hover {
+    opacity: 1;
+    filter: invert(1) brightness(2.5);
+}
+
+/* Alternative approach - use custom image */
+input[type="date"]::-webkit-calendar-picker-indicator {
+    background-image: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="%23c9d1d9" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"><rect x="3" y="4" width="18" height="18" rx="2" ry="2"></rect><line x1="16" y1="2" x2="16" y2="6"></line><line x1="8" y1="2" x2="8" y2="6"></line><line x1="3" y1="10" x2="21" y2="10"></line></svg>');
+    background-repeat: no-repeat;
+    background-size: 16px 16px;
+    width: 16px;
+    height: 16px;
+    cursor: pointer;
+    opacity: 0.7;
+}
+
+input[type="date"]::-webkit-calendar-picker-indicator:hover {
+    opacity: 1;
+}
+
+/* For Firefox and other browsers */
+input[type="date"] {
+    color-scheme: dark;
+    position: relative;
+}
+
+/* Additional fix for all date inputs */
+.date-input,
+input[type="date"] {
+    -webkit-appearance: none;
+    appearance: none;
+    background-color: var(--bg-primary) !important;
+    color: var(--text-primary) !important;
+}
+
+.date-input:hover,
+.user-select:hover {
+    border-color: var(--accent-green);
+}
+
+.date-input:focus,
+.user-select:focus {
+    outline: none;
+    border-color: var(--accent-green);
+    box-shadow: 0 0 0 3px rgba(57, 255, 20, 0.1);
+}
+
+.date-separator {
+    color: var(--accent-green);
+    font-size: 1.5rem;
+    font-weight: bold;
+    padding: 0 5px;
+    margin-top: 25px;
+}
+
+.user-select-group {
+    margin-bottom: 20px;
+}
+
+/* Filter Actions */
+.filter-actions {
+    display: flex;
+    gap: 10px;
+}
+
+.btn-apply,
+.btn-reset {
+    flex: 1;
+    padding: 12px 20px;
+    border: none;
+    border-radius: 6px;
+    font-size: 0.95rem;
+    font-weight: 500;
+    cursor: pointer;
+    transition: all 0.2s ease;
+    display: flex;
+    align-items: center;
+    justify-content: center;
+    gap: 8px;
+}
+
+.btn-apply {
+    background: var(--accent-green);
+    color: var(--bg-primary);
+}
+
+.btn-apply:hover {
+    background: #2bff00;
+    box-shadow: 0 4px 12px rgba(57, 255, 20, 0.3);
+}
+
+.btn-reset {
+    background: transparent;
+    color: var(--text-secondary);
+    border: 1px solid var(--border-color);
+}
+
+.btn-reset:hover {
+    background: var(--bg-primary);
+    color: var(--text-primary);
+    border-color: var(--text-secondary);
+}
+
+/* Generate Reports Card */
+.btn-generate-all {
+    width: 100%;
+    padding: 16px 24px;
+    background: linear-gradient(135deg, var(--accent-green), #2bff00);
+    color: var(--bg-primary);
+    border: none;
+    border-radius: 8px;
+    font-size: 1.05rem;
+    font-weight: 600;
+    cursor: pointer;
+    transition: all 0.3s ease;
+    display: flex;
+    align-items: center;
+    justify-content: center;
+    gap: 10px;
+}
+
+.btn-generate-all:hover {
+    transform: translateY(-2px);
+    box-shadow: 0 6px 20px rgba(57, 255, 20, 0.4);
+}
+
+.or-separator {
+    text-align: center;
+    margin: 20px 0;
+    position: relative;
+}
+
+.or-separator span {
+    background: var(--bg-secondary);
+    padding: 0 15px;
+    color: var(--text-secondary);
+    font-size: 0.85rem;
+    position: relative;
+    z-index: 1;
+}
+
+.or-separator::before {
+    content: '';
+    position: absolute;
+    top: 50%;
+    left: 0;
+    right: 0;
+    height: 1px;
+    background: var(--border-color);
+}
+
+.specific-date-section {
+    margin-top: 20px;
+}
+
+.date-action-group {
+    display: flex;
+    gap: 10px;
+    margin-top: 8px;
+}
+
+.date-action-group .date-input {
+    flex: 1;
+}
+
+.btn-regenerate {
+    padding: 10px 20px;
+    background: var(--bg-primary);
+    color: var(--text-primary);
+    border: 1px solid var(--border-color);
+    border-radius: 6px;
+    cursor: pointer;
+    transition: all 0.2s ease;
+    display: flex;
+    align-items: center;
+    gap: 8px;
+    white-space: nowrap;
+}
+
+.btn-regenerate:hover {
+    background: var(--bg-secondary);
+    border-color: var(--accent-green);
+    color: var(--accent-green);
+}
+
+.btn-icon {
+    font-size: 1.1rem;
+}
+
+/* Responsive Design */
+@media (max-width: 968px) {
+    .controls-container {
+        grid-template-columns: 1fr;
+    }
+    
+    .date-inputs {
+        flex-direction: column;
+        gap: 10px;
+    }
+    
+    .date-separator {
+        display: none;
+    }
+}
+
+@media (max-width: 480px) {
+    .filter-actions {
+        flex-direction: column;
+    }
+    
+    .date-action-group {
+        flex-direction: column;
+    }
+}
+
 .category-badge {
     display: inline-block;
     padding: 4px 8px;
